        Tagmention.stub(:new).with({'these' => 'params'}) { mock_tagmention(:save => true) }
        post :create, :tagmention => {'these' => 'params'}
        assigns(:tagmention).should be(mock_tagmention)
      end

      it "redirects to the created tagmention" do
        Tagmention.stub(:new) { mock_tagmention(:save => true) }
        post :create, :tagmention => {}
        response.should redirect_to(tagmention_url(mock_tagmention))
      end
    end

