        Tagmention.stub(:new).with({'these' => 'params'}) { mock_tagmention(:save => false) }
        post :create, :tagmention => {'these' => 'params'}
        assigns(:tagmention).should be(mock_tagmention)
      end

      it "re-renders the 'new' template" do
        Tagmention.stub(:new) { mock_tagmention(:save => false) }
        post :create, :tagmention => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested tagmention" do
