      it "assigns the tagmention as @tagmention" do
        Tagmention.stub(:find) { mock_tagmention(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:tagmention).should be(mock_tagmention)
      end

      it "re-renders the 'edit' template" do
        Tagmention.stub(:find) { mock_tagmention(:update_attributes => false) }
